import './style.css'
import ReactDOM from 'react-dom/client'
import { Canvas } from '@react-three/fiber'
import Experience from './Experience'
import { useState } from 'react'

const root = ReactDOM.createRoot(document.querySelector('#root'))

const cameraSettings = {
    fov: 45,
    near: 0.1,
    far: 200,
    position: [0, 0, -6]
}

const [position, setPosition] = useState([0, 0, 0]);

  // controlador de eventos de teclado para mover el modelo
  const handleKeyDown = (event) => {
    const speed = 0.1; // velocidad de movimiento
    switch (event.key) {
      case "ArrowUp":
        setPosition([position[0], position[1] + speed, position[2]]);
        break;
      case "ArrowDown":
        setPosition([position[0], position[1] - speed, position[2]]);
        break;
      case "ArrowLeft":
        setPosition([position[0] - speed, position[1], position[2]]);
        break;
      case "ArrowRight":
        setPosition([position[0] + speed, position[1], position[2]]);
        break;
      default:
        break;
    }
  };

root.render(
    <Canvas camera={cameraSettings} shadows={true}  onKeyDown={handleKeyDown}>
        <Experience />
    </Canvas>
)
