import { OrbitControls } from '@react-three/drei'
import { useHelper } from '@react-three/drei'
import Sphere from './Sphere'
import Floor from './Floor'
import { useRef } from 'react'
import { DirectionalLightHelper } from 'three'

export default function Experience() {

    const directionalLightRef = useRef()

    useHelper(directionalLightRef, DirectionalLightHelper, 1)

    const [position, setPosition] = useState([0, 0, 0]);

  // controlador de eventos de teclado para mover el modelo
  const handleKeyDown = (event) => {
    const speed = 0.1; // velocidad de movimiento
    switch (event.key) {
      case "ArrowUp":
        setPosition([position[0], position[1] + speed, position[2]]);
        break;
      case "ArrowDown":
        setPosition([position[0], position[1] - speed, position[2]]);
        break;
      case "ArrowLeft":
        setPosition([position[0] - speed, position[1], position[2]]);
        break;
      case "ArrowRight":
        setPosition([position[0] + speed, position[1], position[2]]);
        break;
      default:
        break;
    }
  };

    return <>

        <OrbitControls makeDefault />

        <directionalLight  ref={directionalLightRef} castShadow position={[0, 10, -6]} intensity={1.5} />
        <ambientLight intensity={0.3} />

        <Sphere/>
        <Floor/>
    </>
}
