import { OrbitControls } from '@react-three/drei'
import { useHelper } from '@react-three/drei'
import Sphere from './Sphere'
import Floor from './Floor'
import { useRef } from 'react'
import { DirectionalLightHelper, HemisphereLightHelper, SpotLightHelper, SpotLight,RectAreaLight} from 'three'

export default function Experience() {

    const directionalLightRef = useRef()
    const hemisphereLightHelper = useRef()
    const spotLightHelper = useRef()
    const rectAreaLight = useRef()

    useHelper(directionalLightRef, DirectionalLightHelper)
    useHelper(hemisphereLightRef, HemisphereLightHelper)
    useHelper(spotLightRef, SpotLightHelper,)
    useHelper(rectAreaLightRef, RectAreaLight)

    return <>
        <OrbitControls makeDefault/>

        <directionalLight  ref={directionalLightRef} 
                           castShadow={true} 
                           position={[0, 3,-5]} 
                           intensity={1.5} 
                           shadow-mapSize={ [ 1024, 1024 ] }
                           color="blue"/>

        {/*<spotLight ref={spotLightRef}
                   position={[0, 10, -5]}
                   color="brown"
                   intensity={1}
                   angle={Math.PI / 20}
                   distance={320}
                   penumbra={0.3}/>*/}

        <ambientLight intensity={0.3} />

        <Sphere/>
        <Floor/>
    </>
}
