import { OrbitControls } from '@react-three/drei'
import { useHelper } from '@react-three/drei'
import Sphere from './Sphere'
import Floor from './Floor'
import { useRef } from 'react'
import { DirectionalLightHelper, HemisphereLightHelper, SpotLightHelper, SpotLight,RectAreaLight} from 'three'

export default function Experience() {

    const directionalLightRef = useRef()
    const hemisphereLightHelper = useRef()
    const spotLightHelper = useRef()
    const rectAreaLight = useRef()

    useHelper(directionalLightRef, DirectionalLightHelper, 2, "yellow")
    useHelper(hemisphereLightHelper, HemisphereLightHelper, 1,"red")
    useHelper(spotLightHelper, SpotLightHelper, 10)
    useHelper(rectAreaLight, RectAreaLight,2,10)

    return <>
        <OrbitControls makeDefault/>
        <spotLight
        ref={spotLightHelper}
        position={[0, 3, -5]}
        color="red"
        intensity={1}
        angle={Math.PI / 4}
        distance={100}
      />
      {spotLightHelper.current && (
        <SpotLightHelper light={spotLightHelper.current} /> // Añade SpotLightHelper como hijo del spotLight
      )}
        {/*<directionalLight  ref={spotLightHelper} castShadow={true} position={[0, 3,-5]} intensity={1.5} shadow-mapSize={ [ 1024, 1024 ] }/>*/}
        <ambientLight intensity={0.3} />

        <Sphere/>
        <Floor/>
    </>
}
